const docHeight=$(document).height();document.addEventListener('DOMContentLoaded',()=>{$('#toTop').hide();$('#toBottom').show()});window.addEventListener('scroll',function(){if($(this).scrollTop()>=1000)$('#toTop').show(500);else if($(this).scrollTop()<1000)$('#toTop').hide(500);if($(this).scrollTop()>=(docHeight-1211))$('#toBottom').hide(500);else if($(this).scrollTop()<(docHeight-1211))$('#toBottom').show(500)});$('#toTop').on('click',()=>{$('html, body').animate({scrollTop:0},800)});$('#toBottom').on('click',()=>{$('html, body').animate({scrollTop:(docHeight-1211)},800)});